INSERT INTO `cms-nette`.page_local (page_local_id, page_id, lang_id, title, url, description, image, video, local_status, content, created, last_edited, author, display_title, display_breadcrumbs) VALUES (1, 1, 1, 'Home Title', 'home-address', 'This is a sample page description.', 0, '', 1, '<section class="home-box" id="heading"><div class="home-box-content animated zoomIn"><h1 style="text-align: center;"><span lang="en"><strong>Multilingual</strong></span><strong>&nbsp;CMS</strong></h1><p style="text-align: center;">Martin Macura</p></div></section><section class="home-box" id="why"><div class="home-box-content"><h2 style="text-align: center;">Why?</h2><div><div class="half-block"><div class="card"><div class="icon"><i class="fa fa-globe"></i></div><div class="content"><h3><span lang="en">Multilingual</span></h3><p>Unlimited amount of translations</p></div></div><div class="card"><div class="icon"><i class="fa fa-rocket"></i></div><div class="content"><h3>Speed</h3><p>AJAX - Modern, effective, fast</p></div></div></div><div class="half-block"><div class="card"><div class="icon"><i class="fa fa-link"></i></div><div class="content"><h3>Permanent links</h3><p>Links change with URLs</p></div></div><div class="card"><div class="icon"><i class="fa fa-pencil"></i></div><div class="content"><h3>Customizability</h3><p>Easy, well-arranged, with a vision</p></div></div></div></div></div></section><section class="home-box" id="more-info"><div class="home-box-content"><h2 style="text-align: center;">More info</h2><p style="text-align: center;"><a href="[link%20page_id=3%20lang_id=1]"><button class="btn">Project specification</button></a> <a href="[link%20page_id=2%20lang_id=1]"><button class="btn">Tutorial <i class="fa fa-long-arrow-right"></i></button></a></p></div></section>', '2018-02-08 23:23:28', '2018-04-23 13:10:33', 1, 0, 0);
